toggleIsLoggedIn = () => {
    this.setState((prevState) => ({
        isLoggedIn : !prevState.isLoggedIn,
    }));
}

    render() {
        return (
            <div>
                <MethodsAsPropsChild 
                isLoggedIn={this.state.isLoggedIn}
                handleSignIn={this.handleSignIn}
                 />
            </div>
        )
    }
}

