import React from "react";

class StatefulGreeting extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      introduction: "Hello!",
      buttonText: "Exit",
    };

  }

  render() {
    return  <h1>Hello, {this.props.name}, {this.props.greeting}</h1>
  }
}

export default StatefulGreeting


